import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';

// Basic configuration via environment variables:
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
const MY_USER_ID = parseInt(process.env.REACT_APP_MY_USER_ID || '1', 10);

export default function App() {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState('');
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [file, setFile] = useState(null);
  const socketRef = useRef(null);
  const messagesRef = useRef(null);

  // --- SOCKET.IO SETUP ---
  useEffect(() => {
    if (!socketRef.current) {
      socketRef.current = io(API_URL, { transports: ['websocket'] });

      socketRef.current.on('connect', () => {
        socketRef.current.emit('join', { userId: MY_USER_ID });
      });

      // Listen for new messages
      socketRef.current.on('new_message', (msg) => {
        const otherId =
          msg.sender_id === MY_USER_ID ? msg.receiver_id : msg.sender_id;
        if (selectedUser && otherId === selectedUser.id) {
          setMessages((prev) => {
            const alreadyExists = prev.some((m) => m.id === msg.id);
            return alreadyExists ? prev : [...prev, msg];
          });
        }
      });

      // Listen for reactions
      socketRef.current.on('new_reaction', (reaction) => {
        setMessages((prev) =>
          prev.map((m) =>
            m.id === reaction.message_id
              ? {
                  ...m,
                  reactions: [...(m.reactions || []), reaction],
                }
              : m
          )
        );
      });
    }

    return () => {
      socketRef.current?.off('new_message');
      socketRef.current?.off('new_reaction');
    };
  }, [selectedUser]);

  // --- AUTO SCROLL ---
  useEffect(() => {
    if (messagesRef.current) {
      messagesRef.current.scrollTop = messagesRef.current.scrollHeight;
    }
  }, [messages]);

  // --- FETCH USERS ---
  async function fetchUsers(q) {
    try {
      const res = await axios.get(`${API_URL}/api/users`, {
        params: { search: q },
      });
      setUsers(res.data);
    } catch (err) {
      console.error('Failed to fetch users', err);
    }
  }

  useEffect(() => {
    fetchUsers('');
  }, []);

  // --- OPEN CONVERSATION ---
  async function openConversation(user) {
    setSelectedUser(user);
    setMessages([]);
    try {
      const res = await axios.get(`${API_URL}/api/messages/${user.id}`, {
        params: { myUserId: MY_USER_ID },
      });
      // Preload empty reactions array
      const enriched = res.data.map((m) => ({ ...m, reactions: [] }));
      setMessages(enriched);
    } catch (err) {
      console.error('Failed to fetch conversation', err);
    }
  }

  // --- FILE UPLOAD HANDLER ---
  async function uploadFile() {
    if (!file) return null;
    const formData = new FormData();
    formData.append('file', file);
    try {
      const res = await axios.post(`${API_URL}/api/upload`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      return res.data.url;
    } catch (err) {
      console.error('File upload failed', err);
      return null;
    }
  }

  // --- SEND MESSAGE ---
  async function sendMessage() {
    if (!selectedUser || (!input.trim() && !file)) return;
    let attachmentUrl = null;
    if (file) {
      attachmentUrl = await uploadFile();
    }
    const payload = {
      sender_id: MY_USER_ID,
      receiver_id: selectedUser.id,
      content: input.trim(),
      attachment_url: attachmentUrl,
    };

    try {
      await axios.post(`${API_URL}/api/messages`, payload);
      setMessages((prev) => [
        ...prev,
        {
          id: Date.now(),
          sender_id: MY_USER_ID,
          receiver_id: selectedUser.id,
          content: input.trim(),
          attachment_url: attachmentUrl,
          timestamp: new Date().toISOString(),
          reactions: [],
        },
      ]);
      setInput('');
      setFile(null);
    } catch (err) {
      console.error('Failed to send message', err);
    }
  }

  // --- ADD REACTION ---
  async function addReaction(messageId, emoji) {
    try {
      await axios.post(`${API_URL}/api/reactions`, {
        message_id: messageId,
        user_id: MY_USER_ID,
        emoji,
      });
    } catch (err) {
      console.error('Failed to add reaction', err);
    }
  }

  return (
    <div className="app" style={{ display: 'flex', height: '100vh' }}>
      {/* --- SIDEBAR --- */}
      <div
        className="sidebar"
        style={{
          width: '25%',
          borderRight: '1px solid #ddd',
          padding: 16,
          background: '#fafafa',
        }}
      >
        <h3>Members</h3>
        <input
          placeholder="Search members..."
          value={search}
          onChange={(e) => {
            setSearch(e.target.value);
            fetchUsers(e.target.value);
          }}
          style={{
            width: '100%',
            padding: 8,
            marginBottom: 12,
          }}
        />
        <div>
          {users.map((u) => (
            <div
              key={u.id}
              onClick={() => openConversation(u)}
              style={{
                padding: 10,
                cursor: 'pointer',
                borderRadius: 6,
                background:
                  selectedUser && selectedUser.id === u.id
                    ? '#e5e7eb'
                    : 'transparent',
                marginBottom: 6,
              }}
            >
              <strong>{u.name}</strong>
              <div style={{ fontSize: 12, color: '#6b7280' }}>{u.email}</div>
            </div>
          ))}
        </div>
      </div>

      {/* --- CHAT AREA --- */}
      <div className="chat" style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        <div
          style={{
            padding: 16,
            borderBottom: '1px solid #e5e7eb',
            background: '#fff',
          }}
        >
          {selectedUser ? (
            <div>
              <strong>{selectedUser.name}</strong>
              <div style={{ fontSize: 12, color: '#6b7280' }}>
                {selectedUser.email}
              </div>
            </div>
          ) : (
            <div>Select a user to start chat</div>
          )}
        </div>

        <div
          className="messages"
          ref={messagesRef}
          style={{ flex: 1, overflowY: 'auto', padding: 16 }}
        >
          {messages.map((m) => (
            <div
              key={m.id}
              className={`message ${m.sender_id === MY_USER_ID ? 'me' : 'them'}`}
              style={{
                marginBottom: 16,
                alignSelf:
                  m.sender_id === MY_USER_ID ? 'flex-end' : 'flex-start',
                background:
                  m.sender_id === MY_USER_ID ? '#d1fae5' : '#e5e7eb',
                borderRadius: 10,
                padding: 10,
                maxWidth: '70%',
              }}
            >
              <div style={{ fontSize: 14 }}>{m.content}</div>

              {m.attachment_url && (
                <div style={{ marginTop: 6 }}>
                  <a
                    href={`${API_URL}${m.attachment_url}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    üìé View Attachment
                  </a>
                </div>
              )}

              <div
                style={{
                  fontSize: 11,
                  color: '#6b7280',
                  marginTop: 6,
                }}
              >
                {new Date(m.timestamp).toLocaleString()}
              </div>

              {/* --- Reaction Buttons --- */}
              <div style={{ marginTop: 6 }}>
                <button
                  onClick={() => addReaction(m.id, 'üëç')}
                  style={{ marginRight: 4 }}
                >
                  üëç
                </button>
                <button
                  onClick={() => addReaction(m.id, '‚ù§Ô∏è')}
                  style={{ marginRight: 4 }}
                >
                  ‚ù§Ô∏è
                </button>
                <button onClick={() => addReaction(m.id, 'üòÇ')}>üòÇ</button>
              </div>

              {/* --- Show Reactions --- */}
              {m.reactions && m.reactions.length > 0 && (
                <div
                  style={{
                    fontSize: 12,
                    marginTop: 4,
                    color: '#4b5563',
                  }}
                >
                  {m.reactions.map((r) => r.emoji).join(' ')}
                </div>
              )}
            </div>
          ))}
        </div>

        {/* --- INPUT AREA --- */}
        <div
          className="input-row"
          style={{
            display: 'flex',
            alignItems: 'center',
            borderTop: '1px solid #e5e7eb',
            padding: 10,
            background: '#fff',
          }}
        >
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            style={{ marginRight: 8 }}
          />
          <input
            placeholder="Type a message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === 'Enter') sendMessage();
            }}
            style={{ flex: 1, padding: 8 }}
          />
          <button className="btn" onClick={sendMessage} style={{ marginLeft: 8 }}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
}
