import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
const MY_USER_ID = parseInt(process.env.REACT_APP_MY_USER_ID || '1', 10);

export default function App() {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState('');
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [file, setFile] = useState(null);
  const [showPicker, setShowPicker] = useState(null); // messageId for reactions
  const socketRef = useRef(null);
  const messagesRef = useRef(null);

  useEffect(() => {
    if (!socketRef.current) {
      socketRef.current = io(API_URL, { transports: ['websocket'] });

      socketRef.current.on('connect', () => {
        socketRef.current.emit('join', { userId: MY_USER_ID });
      });

      socketRef.current.on('new_message', (msg) => {
        const otherId =
          msg.sender_id === MY_USER_ID ? msg.receiver_id : msg.sender_id;
        if (selectedUser && otherId === selectedUser.id) {
          setMessages((prev) => {
            const exists = prev.some((m) => m.id === msg.id);
            return exists ? prev : [...prev, { ...msg, reactions: [] }];
          });
        }
      });

      socketRef.current.on('new_reaction', (reaction) => {
        setMessages((prev) =>
          prev.map((m) =>
            m.id === reaction.message_id
              ? {
                  ...m,
                  reactions: [
                    ...(m.reactions || []).filter(
                      (r) => r.user_id !== reaction.user_id
                    ),
                    reaction,
                  ],
                }
              : m
          )
        );
      });
    }

    return () => {
      socketRef.current?.off('new_message');
      socketRef.current?.off('new_reaction');
    };
  }, [selectedUser]);

  useEffect(() => {
    if (messagesRef.current)
      messagesRef.current.scrollTop = messagesRef.current.scrollHeight;
  }, [messages]);

  async function fetchUsers(q = '') {
    try {
      const res = await axios.get(`${API_URL}/api/users`, { params: { search: q } });
      setUsers(res.data);
    } catch (err) {
      console.error('Failed to fetch users', err);
    }
  }

  useEffect(() => {
    fetchUsers('');
  }, []);

  async function openConversation(user) {
    setSelectedUser(user);
    setMessages([]);
    const res = await axios.get(`${API_URL}/api/messages/${user.id}`, {
      params: { myUserId: MY_USER_ID },
    });
    const enriched = res.data.map((m) => ({ ...m, reactions: [] }));
    setMessages(enriched);
  }

  async function uploadFile() {
    if (!file) return null;
    const formData = new FormData();
    formData.append('file', file);
    const res = await axios.post(`${API_URL}/api/upload`, formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });
    return res.data.url;
  }

  async function sendMessage() {
    if (!selectedUser || (!input.trim() && !file)) return;
    const attachmentUrl = file ? await uploadFile() : null;
    const payload = {
      sender_id: MY_USER_ID,
      receiver_id: selectedUser.id,
      content: input.trim(),
      attachment_url: attachmentUrl,
    };
    await axios.post(`${API_URL}/api/messages`, payload);
    setMessages((prev) => [
      ...prev,
      {
        id: Date.now(),
        sender_id: MY_USER_ID,
        receiver_id: selectedUser.id,
        content: input.trim(),
        attachment_url: attachmentUrl,
        timestamp: new Date().toISOString(),
        reactions: [],
      },
    ]);
    setInput('');
    setFile(null);
  }

  async function addReaction(messageId, emoji) {
    setShowPicker(null);
    await axios.post(`${API_URL}/api/reactions`, {
      message_id: messageId,
      user_id: MY_USER_ID,
      emoji,
    });
  }

  // Helper to preview image
  function renderAttachment(m) {
    if (!m.attachment_url) return null;
    const url = `${API_URL}${m.attachment_url}`;
    const ext = url.split('.').pop().toLowerCase();
    if (['jpg', 'jpeg', 'png', 'gif'].includes(ext)) {
      return <img src={url} alt="attachment" style={{ width: 180, borderRadius: 8, marginTop: 6 }} />;
    }
    if (['mp4', 'mov', 'webm'].includes(ext)) {
      return <video src={url} controls style={{ width: 200, borderRadius: 8, marginTop: 6 }} />;
    }
    return (
      <a href={url} target="_blank" rel="noopener noreferrer" style={{ color: '#2563eb' }}>
        ðŸ“Ž View file
      </a>
    );
  }

  return (
    <div className="app" style={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar */}
      <div style={{ width: '25%', padding: 16, borderRight: '1px solid #ddd', background: '#fafafa' }}>
        <h3>Members</h3>
        <input
          value={search}
          placeholder="Search..."
          onChange={(e) => {
            setSearch(e.target.value);
            fetchUsers(e.target.value);
          }}
          style={{ width: '100%', marginBottom: 12, padding: 8 }}
        />
        {users.map((u) => (
          <div
            key={u.id}
            onClick={() => openConversation(u)}
            style={{
              padding: 10,
              borderRadius: 8,
              background: selectedUser?.id === u.id ? '#e5e7eb' : 'transparent',
              cursor: 'pointer',
              marginBottom: 8,
            }}
          >
            <strong>{u.name}</strong>
            <div style={{ fontSize: 12, color: '#6b7280' }}>{u.email}</div>
          </div>
        ))}
      </div>

      {/* Chat Area */}
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        <div style={{ padding: 16, borderBottom: '1px solid #ddd', background: '#fff' }}>
          {selectedUser ? (
            <div>
              <strong>{selectedUser.name}</strong>
              <div style={{ fontSize: 12, color: '#6b7280' }}>{selectedUser.email}</div>
            </div>
          ) : (
            <div>Select a user to start chatting</div>
          )}
        </div>

        <div ref={messagesRef} style={{ flex: 1, overflowY: 'auto', padding: 16 }}>
          {messages.map((m) => (
            <div
              key={m.id}
              onMouseEnter={() => setShowPicker(m.id)}
              onMouseLeave={() => setShowPicker(null)}
              style={{
                marginBottom: 16,
                maxWidth: '70%',
                alignSelf: m.sender_id === MY_USER_ID ? 'flex-end' : 'flex-start',
                background: m.sender_id === MY_USER_ID ? '#dcf8c6' : '#fff',
                padding: 10,
                borderRadius: 10,
                position: 'relative',
              }}
            >
              {renderAttachment(m)}
              {m.content && <div style={{ marginTop: 4 }}>{m.content}</div>}

              {showPicker === m.id && (
                <div
                  style={{
                    position: 'absolute',
                    bottom: '100%',
                    left: m.sender_id === MY_USER_ID ? 'auto' : 0,
                    right: m.sender_id === MY_USER_ID ? 0 : 'auto',
                    display: 'flex',
                    background: '#fff',
                    border: '1px solid #ddd',
                    borderRadius: 20,
                    padding: '4px 8px',
                    boxShadow: '0 1px 5px rgba(0,0,0,0.15)',
                  }}
                >
                  {['ðŸ‘', 'â¤ï¸', 'ðŸ˜‚', 'ðŸ˜®', 'ðŸ˜¢', 'ðŸ‘'].map((e) => (
                    <span
                      key={e}
                      style={{ fontSize: 20, cursor: 'pointer', margin: '0 4px' }}
                      onClick={() => addReaction(m.id, e)}
                    >
                      {e}
                    </span>
                  ))}
                </div>
              )}

              {m.reactions?.length > 0 && (
                <div style={{ marginTop: 6, fontSize: 14 }}>
                  {m.reactions.map((r) => r.emoji).join(' ')}
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Input */}
        {selectedUser && (
          <div style={{ display: 'flex', padding: 10, borderTop: '1px solid #ddd', background: '#fff' }}>
            <input type="file" onChange={(e) => setFile(e.target.files[0])} style={{ marginRight: 8 }} />
            <input
              placeholder="Type a message"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
              style={{ flex: 1, padding: 8 }}
            />
            <button onClick={sendMessage} style={{ marginLeft: 8 }}>
              Send
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
